import React, { useState } from 'react';
import { Player } from './Player';
import { PlayerForm } from './PlayerForm';
import { v4 as uuidv4 } from 'uuid';
import { EditPlayerForm } from './EditPlayerForm';

export const FieldWrapper = () => {
  const [players, setPlayers] = useState([]);

  const addPlayer = (playerName) => {
    setPlayers([
      ...players,
      { id: uuidv4(), name: playerName, completed: false, isEditing: false },
    ]);
  };

  const deletePlayer = (id) => setPlayers(players.filter((player) => player.id !== id));

  const toggleComplete = (id) => {
    setPlayers(
      players.map((player) =>
        player.id === id ? { ...player, completed: !player.completed } : player
      )
    );
  };

  const editPlayer = (id) => {
    setPlayers(
      players.map((player) =>
        player.id === id ? { ...player, isEditing: !player.isEditing } : player
      )
    );
  };

  const editPlayerName = (name, id) => {
    setPlayers(
      players.map((player) =>
        player.id === id ? { ...player, name, isEditing: !player.isEditing } : player
      )
    );
  };

  return (
    <div className="FieldWrapper">
      <h1>Football Team Setup</h1>
      <PlayerForm addPlayer={addPlayer} />
      {/* display players */}
      {players.map((player) =>
        player.isEditing ? (
          <EditPlayerForm editPlayer={editPlayerName} player={player} key={player.id} />
        ) : (
          <Player
            key={player.id}
            player={player}
            deletePlayer={deletePlayer}
            editPlayer={editPlayer}
            toggleComplete={toggleComplete}
          />
        )
      )}
    </div>
  );
};
