// Components/FootballField.js
import React from 'react';

export const FootballField = ({ players, editPlayer, deletePlayer, teamColor }) => {
  const fieldStyle = {
    width: '600px',
    height: '400px',
    backgroundColor: '#4CAF50',
    border: '5px solid white',
    position: 'relative',
    margin: '20px auto',
    borderRadius: '10px',
    overflow: 'hidden',
  };

  const lineStyle = {
    position: 'absolute',
    width: '100%',
    height: '2px',
    backgroundColor: 'white',
  };

  const circleStyle = {
    position: 'absolute',
    width: '80px',
    height: '80px',
    borderRadius: '50%',
    border: '2px solid white',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
  };

  return (
    <div style={fieldStyle}>
      {/* Centre du terrain */}
      <div style={circleStyle}></div>
      {/* Lignes de touche */}
      <div style={{ ...lineStyle, top: '50%', transform: 'translateY(-50%)' }}></div>
      {/* Lignes de but */}
      <div style={{ ...lineStyle, width: '2px', height: '100%', left: '5%' }}></div>
      <div style={{ ...lineStyle, width: '2px', height: '100%', right: '5%' }}></div>

      {/* Affichage des joueurs avec les bordures correspondant aux équipes */}
      {players.map((player) => (
        <div
          key={player.id}
          style={{
            position: 'absolute',
            top: `${player.position.y}%`,
            left: `${player.position.x}%`,
            transform: 'translate(-50%, -50%)',
            color: 'white',
            fontWeight: 'bold',
            textShadow: '1px 1px 2px black',
            cursor: 'pointer',
            border: `1px solid ${teamColor === 'red' ? 'red' : 'blue'}`, // Définir la couleur de la bordure selon l'équipe
            padding: '5px',
            borderRadius: '50%',
          }}
          onClick={() => editPlayer(player.id)}
        >
          ⚽ {player.name}
        </div>
      ))}
    </div>
  );
};
