import React from 'react';
import { Player } from './Player';

export const FootballField = ({ players }) => {
  const fieldStyle = {
    width: '600px',
    height: '400px',
    border: '2px solid green',
    position: 'relative',
    margin: '20px auto',
  };

  return (
    <div style={fieldStyle}>
      {players.map((player) => (
        <div
          key={player.id}
          style={{
            position: 'absolute',
            top: `${player.position.y}%`,
            left: `${player.position.x}%`,
            transform: 'translate(-50%, -50%)',
          }}
        >
          âš½ {player.name}
        </div>
      ))}
    </div>
  );
};
